mapper.select:
SELECT
COUNT(*)
FROM
ent_project_achievement_info i_e_p_a_i
LEFT JOIN ent_project_info e_p_i ON i_e_p_a_i.pro_info_id = e_p_i.pro_info_id

     e_p_i.user_per_id IS NOT NULL 
     AND e_p_i.user_per_id = #{userPerId} 
     AND e_p_i.user_teacher_id IS NOT NULL 
     AND e_p_i.user_teacher_id = #{userTeacherId} 
     AND e_p_i.ent_info_id IS NOT NULL 
     AND e_p_i.ent_info_id = #{entInfoId} 
     AND e_p_i.in_apply = #{inApply} 
     AND e_p_i.pro_name LIKE CONCAT('%', #{key},'%')
