e_p_c_i.user_per_id IS NOT NULL AND e_p_c_i.user_per_id = #{user_per_id} AND e_p_c_i.user_teacher_id IS NOT NULL AND e_p_c_i.user_teacher_id = #{user_teacher_id} AND e_p_c_i.ent_info_id IS NOT NULL AND e_p_c_i.ent_info_id = #{ent_info_id} AND e_p_c_i.in_apply = #{inApply} AND e_p_i.pro_name LIKE CONCAT('%', #{key},'%')
limit #{page}, #{pageSize}

SELECT
    COUNT(*)
FROM
ent_project_cooperation_info e_p_c_i
LEFT JOIN ent_project_info e_p_i ON e_p_c_i.pro_info_id = e_p_i.pro_info_id

     e_p_c_i.user_per_id IS NOT NULL 
     AND e_p_c_i.user_per_id = #{user_per_id} 
     AND e_p_c_i.user_teacher_id IS NOT NULL 
     AND e_p_c_i.user_teacher_id = #{user_teacher_id} 
     AND e_p_c_i.ent_info_id IS NOT NULL 
