mapper.select:
SELECT COUNT(*) FROM ent_recruitment_info e_r_i LEFT JOIN ent_enterprise_info e_e_i ON e_r_i.ent_info_id = e_e_i.ent_info_id
e_r_i.in_apply = #{inApply}
AND e_r_i.recruitment_post like concat('%',#{recruitmentPost},'%')

SELECT e_r_i.recruitment_info_id, e_r_i.ent_info_id, e_r_i.job_content, e_r_i.recruitment_post, e_r_i.post_require, e_r_i.recruitment_people_number, e_r_i.recruitment_specialty, e_r_i.work_way, e_r_i.in_apply, e_e_i.ent_info_id AS e_ent_info_id, e_e_i.user_id, e_e_i.ent_name, e_e_i.ent_corporate, e_e_i.ent_business, e_e_i.ent_code, e_e_i.ent_register, e_e_i.ent_founding_time, e_e_i.ent_type, e_e_i.ent_status, e_e_i.ent_register_address, e_e_i.ent_address, e_e_i.ent_introduce, e_e_i.new_high_zones, e_e_i.ent_logo, e_e_i.ent_in_time, e_e_i.in_apply AS e_in_apply FROM ent_recruitment_info e_r_i LEFT JOIN ent_enterprise_info e_e_i ON e_r_i.ent_info_id = e_e_i.ent_info_id
e_r_i.in_apply = #{inApply}
AND e_r_i.recruitment_post like concat('%',#{recruitmentPost},'%')
limit #{page}, #{pageSize}
